import axios from "axios";
import { returnErrors } from "./errorActions";

import { GET_MY_POSTS, GET_POSTS, GET_POST } from "./types";

import { APIURL } from "../config/vars";

export const getPost = (postId) => (dispatch) => {
  axios
    .get(`${APIURL}posts/${postId}`)
    .then((res) => {
      dispatch({
        type: GET_POST,
        payload: res.data.data,
      });
    })
    .catch((err) => {
      dispatch(console.log(err));
    });
};

export const getMyPosts = () => (dispatch, getState) => {
  const user = getState().auth.user;
  if (user) {
    axios
      .get(`${APIURL}posts?user=${user._id}`)
      .then((res) => {
        dispatch({
          type: GET_MY_POSTS,
          payload: res.data.data,
        });
      })
      .catch((err) => {
        dispatch(returnErrors(err.response.data, err.response.status));
      });
  }
};

export const getPosts = () => (dispatch) => {
  axios
    .get(`${APIURL}posts`)
    .then((res) => {
      dispatch({
        type: GET_POSTS,
        payload: res.data.data,
      });
    })
    .catch((err) => {
      dispatch(returnErrors(err.response.data, err.response.status));
    });
};

export const upVote = (post) => async (dispatch, getState) => {
  // Check if user has already upvoted
  const user = getState().auth.user._id;

  const checkUpVote = await axios.post(`${APIURL}check/upvote`, {
    post: post._id,
    user: user,
  });

  // If user has upvoted then remove upvote
  if (!checkUpVote.data.success) {
    console.log("note upvoted");
  } else {
    console.log("up voted");
  }

  // Else upvote
};
