import React, { useState } from "react";

import Input from "../../UI/Input";
import Button from "../../UI/Button";
import Link from "../../UI/Link";
import Loading from "../../UI/Loading";
import ImageUpload from "../../UI/ImageUpload";
import ImageBox from "../../UI/ImageBox";
import Colors from "../../../config/colors";
import { useDispatch } from "react-redux";
import { store } from "react-notifications-component";
import { MdClose } from "react-icons/md";

export const FormFooter = () => {
  return (
    <div style={styles.link}>
      <Link style={styles.linkBtn} to="/">
        <Button
          style={{ ...styles.footerBtn, backgroundColor: Colors.tertiary }}
          title="Cancel"
        />
      </Link>
    </div>
  );
};

const FormBody = (props) => {
  const [title, setTitle] = useState("");
  const [isSelected, setIsSelected] = useState(false);
  const [fileURL, setFileURL] = useState(null);
  const [loading, setLoading] = useState(false);

  const dispatch = useDispatch();

  const handlePost = () => {};

  const handleFile = (fileURL) => {
    setFileURL(fileURL);
  };

  const handleRemoveFile = () => {
    setFileURL(null);
  };

  const handleTitleChange = (e) => {
    setTitle(e.target.value);
  };

  return (
    <div>
      <div style={styles.selectFile}>
        {fileURL ? (
          <div>
            <ImageBox
              src={fileURL}
              alt="meme"
              handleCancel={handleRemoveFile}
              handleFile={handleFile}
            />
          </div>
        ) : (
          <ImageUpload required={true} handleFile={handleFile} />
        )}
      </div>
      <Input
        value={title}
        onChange={handleTitleChange}
        type="text"
        placeholder="Title"
        style={styles.input}
      />
      {loading ? (
        <Loading />
      ) : (
        <Button style={styles.button} onClick={handlePost} title="Post" />
      )}
    </div>
  );
};

const styles = {
  button: {
    margin: 5,
    width: "90%",
    backgroundColor: Colors.primary,
  },
  input: {
    margin: 5,
    width: "90%",
  },
  form: {
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    flexDirection: "column",
  },
  selectFile: {
    width: "100%",
    display: "flex",
    justifyContent: "center",
    marginTop: 20,
    marginBottom: 20,
  },
  linkText: {
    fontSize: 12,
    margin: 4,
    color: "#f1f1f1",
  },
  link: {
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    width: "100%",
  },
  linkBtn: {
    marginLeft: 2,
    marginRight: 2,
    width: "100%",
  },
  footerBtn: {
    marginTop: 20,
    marginBottom: 20,
    width: "90%",
  },
};

export default FormBody;
