import React from "react";

import globalStyles from "../../config/globalStyles";

const MemeCard = (props) => {
  const { post } = props;

  return (
    <div style={styles.card}>
      <h4 style={styles.title}>{post.title}</h4>
      <div>
        {post.content.endsWith("mp4") ||
        post.content.endsWith("ogg") ||
        post.content.endsWith("webm") ? (
          <video src={post.content} controls />
        ) : (
          <img src={post.content} />
        )}
      </div>
      <p style={(styles.category, globalStyles.hint)}>{post.category}</p>
    </div>
  );
};

const styles = {
  card: {
    backgroundColor: "white",
    paddingTop: 10,
    paddingBottom: 10,
    paddingRight: 20,
    paddingLeft: 20,
    borderRadius: globalStyles.borderRadius,
    boxShadow: globalStyles.boxShadow,
  },
  title: {
    marginTop: 10,
    marginBottom: 10,
  },
  category: {
    margin: 0,
    padding: 0,
  },
};

export default MemeCard;
