import React, { useState } from "react";
import Colors from "../../config/colors";
import { Route, Redirect } from "react-router-dom";

import { APIURL } from "../../config/vars";

import { useDispatch, useSelector } from "react-redux";
import { loadUser, login } from "../../actions/authActions";

import axios from "axios";

const Login = (props) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [errorMsg, setErrorMsg] = useState("");

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const dispatch = useDispatch();

  const auth = useSelector(
    (state) => ({
      auth: state.auth,
    }),
    []
  );

  if (auth.auth.token) {
    return (
      <Route>
        <Redirect to="/" />
      </Route>
    );
  }

  const handleLogin = (e) => {
    e.preventDefault();

    const body = {
      email: email,
      password: password,
    };

    axios
      .post(`${APIURL}auth/login`, body)
      .then((res) => {
        // Redirect to home

        // dispatch login
        dispatch(login(res.data.token));
        dispatch(loadUser());

        setErrorMsg("");
        setEmail("");
        setPassword("");
        props.history.push("/");
      })
      .catch((err) => {
        setErrorMsg("Invalid Credentials");
      });
  };

  return (
    <div style={styles.container}>
      <form onSubmit={handleLogin} style={styles.form}>
        <h6 style={styles.errorMsg}>{errorMsg}</h6>
        <label style={{ color: "#575656" }}>Email</label>
        <input
          value={email}
          required
          onChange={handleEmailChange}
          type="email"
        />
        <label style={{ color: "#575656" }}>Password</label>
        <input
          value={password}
          required
          onChange={handlePasswordChange}
          type="password"
        />
        <div style={styles.footer}>
          <h6 style={{ color: "#acaca9" }}>Need an account? Sign Up!</h6>
          <h6 style={{ color: "#acaca9" }}>Sign in as guest</h6>
          <h6 style={{ color: "#acaca9" }}>Forgot Password?</h6>
          <button style={styles.button} type="submit">
            <b>LOGIN</b>
          </button>
        </div>
      </form>
    </div>
  );
};

const styles = {
  container: {
    height: "100vh",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
  },
  errorMsg: {
    color: Colors.warningColor,
    paddingBottom: "5px",
    textAlign: "center",
  },
  button: {
    marginTop: "10px",
    paddingRight: 10,
    paddingLeft: 10,
    paddingTop: 5,
    paddingBottom: 5,
    color: "white",
    backgroundColor: Colors.secondaryColor,
    border: "none",
  },
  footer: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
  },
};

export default Login;
