import axios from "axios";
import { returnErrors } from "./errorActions";

import { GET_MY_POSTS, GET_POSTS, GET_POST, UP_VOTE, DOWN_VOTE } from "./types";

import { APIURL } from "../config/vars";

export const getPost = (postId) => (dispatch) => {
  axios
    .get(`${APIURL}posts/${postId}`)
    .then((res) => {
      dispatch({
        type: GET_POST,
        payload: res.data.data,
      });
    })
    .catch((err) => {
      dispatch(console.log(err));
    });
};

export const getMyPosts = () => (dispatch, getState) => {
  const user = getState().auth.user;
  if (user) {
    axios
      .get(`${APIURL}posts?user=${user._id}`)
      .then((res) => {
        dispatch({
          type: GET_MY_POSTS,
          payload: res.data.data,
        });
      })
      .catch((err) => {
        dispatch(returnErrors(err.response.data, err.response.status));
      });
  }
};

export const getPosts = () => (dispatch) => {
  axios
    .get(`${APIURL}posts`)
    .then((res) => {
      dispatch({
        type: GET_POSTS,
        payload: res.data.data,
      });
    })
    .catch((err) => {
      dispatch(returnErrors(err.response.data, err.response.status));
    });
};

export const upVote = (post) => async (dispatch, getState) => {
  const user = getState().auth;
  const upVotes = getState().posts.upVotes;
  const downVotes = getState().posts.downVotes;

  const upVote = await axios.post(`${APIURL}votes/upvotepost`, {
    post: post._id,
    user: user.user._id,
  });

  if (upVote.data.success) {
    // Add to local up vote state
    const newUpVotes = [...upVotes, user.user._id];

    dispatch({
      type: UP_VOTE,
      payload: newUpVotes,
    });

    // remove from local down vote state if neccessary
    let newDownVotes = [];

    downVotes.forEach((vote) => {
      if (vote !== user.user._id) {
        newDownVotes.push(vote);
      }
    });

    dispatch({
      type: DOWN_VOTE,
      payload: newDownVotes,
    });
  } else {
    // remove from local up vote state
    let newUpVotes = [];

    upVotes.forEach((vote) => {
      if (vote !== user.user._id) {
        newUpVotes.push(vote);
      }
    });

    dispatch({
      type: UP_VOTE,
      payload: newUpVotes,
    });
    // remove up vote with put request
    await axios.put(`${APIURL}votes/upvotepost`, {
      post: post._id,
      user: user.user._id,
    });
  }
};
