import React, { useState } from "react";

import colors from "../../config/colors";

import { useMediaPredicate } from "react-media-hook";

import { MdSearch, MdClose } from "react-icons/md";

import ImageUploadBtn from "../UI/ImageUploadBtn";

const ImageBox = (props) => {
  const smallScreen = useMediaPredicate("(max-width: 600px)");
  const mediumScreen = useMediaPredicate("(max-width: 1200px)");

  return (
    <div
      style={
        smallScreen
          ? {
              background: `url(${props.src})`,
              ...styles.imgContainer,
              ...styles.imgSm,
            }
          : mediumScreen
          ? {
              background: `url(${props.src})`,
              ...styles.imgContainer,
              ...styles.imgMd,
            }
          : {
              background: `url(${props.src})`,
              ...styles.imgContainer,
              ...styles.imgLg,
            }
      }
    >
      <div style={styles.edit}>
        <MdClose style={styles.editIcon} onClick={props.handleCancel} />
        <ImageUploadBtn handleFile={props.handleFile} photo={props.photo} />
      </div>
      {hover ? (
        <div style={styles.hoverOverlay} onClick={props.handleOpen}>
          <MdSearch style={styles.icon} />
        </div>
      ) : null}
    </div>
  );
};

const styles = {
  imgContainer: {
    border: `1px solid ${colors.secondary}`,
    borderRadius: 10,
    cursor: "pointer",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    overflow: "hidden",
    backgroundPosition: "center",
    backgroundRepeat: "no-repeat",
    backgroundSize: "cover",
    position: "relative",
  },
  imgLg: {
    height: "10vw",
    width: "10vw",
  },
  imgMd: {
    height: "30vw",
    width: "30vw",
  },
  imgSm: {
    height: "50vw",
    width: "50vw",
  },
  hoverOverlay: {
    width: "100%",
    height: "100%",
    background: "rgb(0,0,0,0.5)",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
  },
  icon: {
    height: 28,
    width: "auto",
    color: colors.secondary,
  },
  edit: {
    position: "absolute",
    top: 0,
    left: 0,
    backgroundColor: colors.secondary,
    width: "100%",
    display: "flex",
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",
  },
  editIcon: {
    padding: 5,
    color: "white",
    height: 18,
    width: "auto",
  },
};

export default ImageBox;
