import {
  GET_MY_POSTS,
  GET_POSTS,
  GET_POST,
  UP_VOTE,
  DOWN_VOTE,
} from "../actions/types";

const initialState = {
  myPosts: [],
  posts: [],
  post: null,
  upVotes: [],
  downVotes: [],
};

export default function (state = initialState, action) {
  switch (action.type) {
    case GET_POST:
      return {
        ...state,
        post: action.payload,
      };
    case GET_MY_POSTS:
      return {
        ...state,
        myPosts: action.payload,
      };
    case GET_POSTS:
      return {
        ...state,
        posts: action.payload,
      };
    case UP_VOTE:
      return {
        ...state,
        upVotes: action.payload,
      };
    case DOWN_VOTE:
      return {
        ...state,
        downVotes: action.payload,
      };
    default:
      return state;
  }
}
