import { GET_MY_POSTS, GET_POSTS, GET_POST, LIKE_POST } from "../actions/types";

const initialState = {
  myPosts: [],
  posts: [],
  post: null,
  likes: [],
};

export default function (state = initialState, action) {
  switch (action.type) {
    case GET_POST:
      return {
        ...state,
        post: action.payload,
      };
    case GET_MY_POSTS:
      return {
        ...state,
        myPosts: action.payload,
      };
    case GET_POSTS:
      return {
        ...state,
        posts: action.payload,
      };
    case LIKE_POST:
      return {
        ...state,
        likes: action.payload,
      };
    default:
      return state;
  }
}
