import React, { useState } from "react";

import Navbar from "../../components/UI/Navbar";
import Backdrop from "../../components/UI/Backdrop";
import SideMenu from "../../components/UI/SideMenu";
import Loading from "../../components/UI/Loading";
import Button from "../../components/UI/Button";

import globalStyles from "../../config/globalStyles";
import { useSelector, useDispatch } from "react-redux";
import { MdPerson, MdPersonAdd } from "react-icons/md";
import { useHistory } from "react-router-dom";
import { logout } from "../../actions/authActions";

const Home = (props) => {
  const dispatch = useDispatch();
  const history = useHistory();
  const [toggleSideMenu, setToggleSideMenu] = useState(false);

  const handleSideMenu = () => {
    setToggleSideMenu(!toggleSideMenu);
  };

  const handleLogout = () => {
    dispatch(logout());
    history.push(`/login`);
  };

  const auth = useSelector(
    (state) => ({
      auth: state.auth,
    }),
    []
  );

  // if (!posts.posts) {
  //   return (
  //     <div style={globalStyles.fullScreenLoading}>
  //       <Loading />
  //     </div>
  //   );
  // }

  const Footer = () => {
    return (
      <Button
        style={styles.footerBtn}
        title="Logout"
        handlePress={handleLogout}
      />
    );
  };

  const bodyLinks = [
    {
      title: "Login",
      to: "/login",
      icon: <MdPerson style={globalStyles.icon} />,
    },
    {
      title: "Signup",
      to: "/signup",
      icon: <MdPersonAdd style={globalStyles.icon} />,
    },
  ];

  return (
    <div style={styles.container}>
      <Navbar onClick={handleSideMenu} />
      {toggleSideMenu ? <Backdrop onClick={handleSideMenu} /> : null}
      {!auth.auth.token ? (
        <SideMenu isOpen={toggleSideMenu} bodyLinks={bodyLinks} />
      ) : null}
      {auth.auth.token ? (
        <SideMenu
          isOpen={toggleSideMenu}
          bodyLinks={bodyLinks}
          footer={<Footer />}
        />
      ) : null}
    </div>
  );
};

const styles = {
  container: {
    width: "100vw",
  },
};

export default Home;
