import React, { useEffect } from "react";

import globalStyles from "../../config/globalStyles";
import { useSelector, useDispatch } from "react-redux";
import { getPosts } from "../../actions/postActions";
import Loading from "../../components/UI/Loading";
import MemeCard from "../../components/UI/MemeCard";
import {
  disableBodyScroll,
  enableBodyScroll,
  clearAllBodyScrollLocks,
} from "body-scroll-lock";

const All = (props) => {
  const dispatch = useDispatch();
  useEffect(() => {
    dispatch(getPosts());
  }, [dispatch]);

  const posts = useSelector(
    (state) => ({
      posts: state.posts.posts,
    }),
    []
  );

  const disableScroll = document.querySelector("#disableScroll");
  const enableScroll = document.querySelector("#enableScroll");

  disableBodyScroll(disableScroll);
  enableBodyScroll(enableScroll);

  if (!posts.posts) {
    return (
      <div style={globalStyles.fullScreenLoading}>
        <Loading />
      </div>
    );
  }

  return (
    <div style={styles.container} id="enableScroll">
      <div style={styles.content} id="enableScroll">
        {posts.posts.map((post, i) => {
          return (
            <React.Fragment key={i}>
              <MemeCard post={post} />
            </React.Fragment>
          );
        })}
      </div>
    </div>
  );
};

const styles = {
  container: {
    paddingLeft: "35vw",
    paddingRight: "35vw",
    height: "calc(100vh - 56px)",
    overFlow: "hidden",
    marginTop: "56px",
  },
  content: {
    display: "flex",
    flexDirection: "column",
    justifyContent: "center",
  },
};

export default All;
