import React, { useState } from "react";

import Navbar from "../../components/UI/Navbar";
import Backdrop from "../../components/UI/Backdrop";
import SideMenu from "../../components/UI/SideMenu";
import Loading from "../../components/UI/Loading";
import Button from "../../components/UI/Button";
import Link from "../../components/UI/Link";

import globalStyles from "../../config/globalStyles";
import { useSelector, useDispatch } from "react-redux";
import { MdPerson, MdPersonAdd, MdCreate } from "react-icons/md";
import { useHistory } from "react-router-dom";
import { logout } from "../../actions/authActions";

const Home = (props) => {
  const dispatch = useDispatch();
  const history = useHistory();
  const [toggleSideMenu, setToggleSideMenu] = useState(false);

  const handleSideMenu = () => {
    setToggleSideMenu(!toggleSideMenu);
  };

  const handleLogout = () => {
    dispatch(logout());
    history.push(`/login`);
  };

  const auth = useSelector(
    (state) => ({
      auth: state.auth,
    }),
    []
  );

  // if (!posts.posts) {
  //   return (
  //     <div style={globalStyles.fullScreenLoading}>
  //       <Loading />
  //     </div>
  //   );
  // }

  const Footer = () => {
    return (
      <React.Fragment>
        <Link style={styles.links} to="/funny">
          <MdPerson style={globalStyles.icon} />
          <h3>
            <b>Funny</b>
          </h3>
        </Link>
        <Button
          style={styles.footerBtn}
          title="Logout"
          handlePress={handleLogout}
        />
      </React.Fragment>
    );
  };

  const bodyLinks = [
    {
      title: "Login",
      to: "/login",
      icon: <MdPerson style={globalStyles.icon} />,
    },
    {
      title: "Signup",
      to: "/signup",
      icon: <MdPersonAdd style={globalStyles.icon} />,
    },
  ];

  const authBodyLinks = [
    {
      title: "Profile",
      to: "/profile",
      icon: <MdPerson style={globalStyles.icon} />,
    },
    {
      title: "New Post",
      to: "/new",
      icon: <MdCreate style={globalStyles.icon} />,
    },
  ];

  return (
    <div style={styles.container}>
      <Navbar onClick={handleSideMenu} />
      {toggleSideMenu ? <Backdrop onClick={handleSideMenu} /> : null}
      {!auth.auth.token ? (
        <SideMenu isOpen={toggleSideMenu} bodyLinks={bodyLinks} />
      ) : null}
      {auth.auth.token ? (
        <SideMenu
          isOpen={toggleSideMenu}
          bodyLinks={authBodyLinks}
          footer={<Footer />}
        />
      ) : null}
    </div>
  );
};

const styles = {
  container: {
    width: "100vw",
  },
  links: {
    textDecoration: "none",
    color: "white",
    display: "flex",
    justifyContent: "flex-start",
    // marginLeft: 40,
    alignItems: "center",
  },
  footerBtn: {
    boxShadow: "none",
  },
};

export default Home;
