import {
  GET_MY_POSTS,
  GET_POSTS,
  GET_POST,
  LOAD_MORE_POSTS,
} from "../actions/types";

const initialState = {
  myPosts: [],
  posts: [],
  post: null,
  page: 1,
};

export default function (state = initialState, action) {
  switch (action.type) {
    case GET_POST:
      return {
        ...state,
        post: action.payload,
      };
    case GET_MY_POSTS:
      return {
        ...state,
        myPosts: action.payload,
      };
    case GET_POSTS:
      return {
        ...state,
        posts: action.payload,
      };
    case LOAD_MORE_POSTS:
      return {
        ...state,
        posts: action.payload,
        page: action.page,
      };
    default:
      return state;
  }
}
