import React, { useEffect, useState } from "react";

import globalStyles from "../../config/globalStyles";
import { useSelector, useDispatch } from "react-redux";
import { getPosts } from "../../actions/postActions";
import { useMediaPredicate } from "react-media-hook";
import Loading from "../../components/UI/Loading";
import MemeCard from "../../components/UI/MemeCard";
import Button from "../../components/UI/Button";
import Link from "../../components/UI/Link";
import Modal from "../../components/UI/Modal";
import Backdrop from "../../components/UI/Backdrop";
import colors from "../../config/colors";

const All = (props) => {
  const dispatch = useDispatch();
  const biggerThan1024 = useMediaPredicate("(min-width: 1024px)");
  const biggerThan720 = useMediaPredicate("(min-width: 720px)");
  const [toggleModal, setToggleModal] = useState(false);

  const handleToggleModal = () => {
    setToggleModal(!toggleModal);
  };

  useEffect(() => {
    dispatch(getPosts());
  }, [dispatch]);

  const posts = useSelector(
    (state) => ({
      posts: state.posts.posts,
    }),
    []
  );

  if (!posts.posts.length) {
    return (
      <div style={globalStyles.fullScreenLoading}>
        <Loading />
      </div>
    );
  }

  const ModalBody = () => {
    return (
      <div>
        <p>
          Meme Share aims to be the internet's number one source for all the
          latest and greatest memes.
          <br />
          If you're interested in interacting with our wonderful community, you
          can sign up for free down below!
        </p>
        <div>
          <Link to="/login">
            <Button
              title="Login"
              style={{ backgroundColor: colors.green, marginLeft: 10 }}
            />
          </Link>
          <Link to="/signup">
            <Button
              title="Signup"
              style={{ backgroundColor: colors.tertiary }}
            />
          </Link>
        </div>
      </div>
    );
  };

  return (
    <React.Fragment>
      <Modal
        header={<h1>Hey there!</h1>}
        body={<ModalBody />}
        close={handleToggleModal}
        isOpen={toggleModal}
      />
      {toggleModal ? <Backdrop /> : null}
      {biggerThan1024 ? (
        <div style={styles.container}>
          <div style={styles.content}>
            {posts.posts.map((post, i) => {
              return (
                <React.Fragment key={i}>
                  <MemeCard toggleModal={handleToggleModal} post={post} />
                </React.Fragment>
              );
            })}
          </div>
        </div>
      ) : biggerThan720 ? (
        <div style={styles.mediumContainer}>
          <div style={styles.content}>
            {posts.posts.map((post, i) => {
              return (
                <React.Fragment key={i}>
                  <MemeCard toggleModal={handleToggleModal} post={post} />
                </React.Fragment>
              );
            })}
          </div>
        </div>
      ) : (
        <div style={styles.smallContainer}>
          <div style={styles.content}>
            {posts.posts.map((post, i) => {
              return (
                <React.Fragment key={i}>
                  <MemeCard toggleModal={handleToggleModal} post={post} />
                </React.Fragment>
              );
            })}
          </div>
        </div>
      )}
    </React.Fragment>
  );
};

const styles = {
  container: {
    marginLeft: "35vw",
    marginRight: "35vw",
    height: "calc(100vh - 56px)",
    marginTop: "56px",
  },
  mediumContainer: {
    marginLeft: "20vw",
    marginRight: "20vw",
    height: "calc(100vh - 56px)",
    marginTop: "56px",
  },
  smallContainer: {
    marginLeft: "5vw",
    marginRight: "5vw",
    height: "calc(100vh - 56px)",
    marginTop: "56px",
  },
  content: {
    maxWidth: "90vw",
    display: "flex",
    flexDirection: "column",
    justifyContent: "center",
    paddingBottom: 20,
  },
};

export default All;
