import React from "react";

import globalStyles from "../../config/globalStyles";
import { MdKeyboardArrowUp, MdKeyboardArrowDown, MdChat } from "react-icons/md";

const MemeCard = (props) => {
  const { post } = props;

  return (
    <div style={styles.card}>
      <p style={{ ...styles.category, ...globalStyles.hint }}>
        {post.category.charAt(0).toUpperCase() + post.category.slice(1)}
      </p>
      <h4 style={styles.title}>{post.title}</h4>
      <div>
        {post.content.endsWith("mp4") ||
        post.content.endsWith("ogg") ||
        post.content.endsWith("webm") ? (
          <video src={post.content} style={styles.content} controls />
        ) : (
          <img src={post.content} style={styles.content} />
        )}
      </div>
      <div style={styles.interactions}>
        <div style={styles.interaction}>
          <MdKeyboardArrowUp style={globalStyles.icon} />
          <p>{post.upVotes.length}</p>
        </div>
        <div style={styles.interaction}>
          <MdKeyboardArrowDown style={globalStyles.icon} />
          <p>{post.downVotes.length}</p>
        </div>
        <div style={styles.interaction}>
          <MdChat style={globalStyles.icon} />
          <p>{post.comments.length}</p>
        </div>
      </div>
    </div>
  );
};

const styles = {
  card: {
    backgroundColor: "white",
    paddingTop: 10,
    paddingBottom: 10,
    paddingRight: 20,
    paddingLeft: 20,
    borderRadius: globalStyles.borderRadius,
    boxShadow: globalStyles.boxShadow,
    width: "100%",
  },
  title: {
    marginTop: 10,
    marginBottom: 10,
  },
  category: {
    marginTop: 0,
    marginBottom: 10,
  },
  content: {
    width: "100%",
    height: "auto",
    borderRadius: globalStyles.borderRadius,
    boxShadow: globalStyles.boxShadow,
  },
  interactions: {
    display: "flex",
    flexDirection: "row",
    marginTop: 20,
    marginBottom: 10,
  },
  interaction: {
    borderRadius: globalStyles.borderRadius,
    boxShadow: globalStyles.boxShadow,
    // border: "1px solid grey",
    paddingLeft: 10,
    paddingRight: 10,
    marginLeft: 5,
    display: "flex",
    flexDirection: "row",
    justifyContent: "center",
    alignItems: "center",
  },
};

export default MemeCard;
